<template>
  <div>
   <header-bar></header-bar>
    <loading v-if="isLoading"></loading>
    <div class="search">
     <div class="content">
       <p>影视</p>
       <ul class="result">
         <li v-for="sub in subjects" :key="sub.id">
          <router-link :to="{name:'MovieView',params:{id:sub.id}}" class="toDetail">
            <div class="ResImg">
              <img :src="sub.images.small" alt="" width="40" height="40">
            </div>
            <div class="ResTxt">
              <p>{{sub.title}}</p>
              <rating v-if="sub.rating" :rating="sub.rating"></rating>
            </div>
          </router-link>
         </li>
       </ul>
       <!--到底了-->
       <p class="end">已显示所有影视搜索结果</p>
     </div>
    </div>
  </div>
</template>

<script>
  import Rating from '../../public/Rating'
  import Loading from '../../public/Loading'
  import HeaderBar from '../HeaderBar'
  export default {
    name: 'SearchView',
    data(){
      return{
        subjects:[],
        isLoading:true
      }
    },
    components:{
      Rating,
      Loading,
      HeaderBar
    },
    created(){
      const val = this.$route.params.val
      this.axios.get('/api/movie/search?q='+val)
        .then((res)=>{
          this.isLoading = false
          let subjects = res.data.subjects
          this.subjects = subjects
          // console.log(res.data)
        })
        .catch((err)=>console.log(err))
    }
  }
</script>

